import React, { useState } from "react";

function CreateBlogs() {
  const [create_data, setCreate] = useState({
    title: "",
    description: "",
    ingredients: "",
    instructions: "",
  });
  const [imageFile, setImageFile] = useState(null); // New state for image file

  // Handle form submission
  const submit = () => {
    create_Blog();
  };

  // Handle text input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCreate({ ...create_data, [name]: value });
  };

  // Handle image file input change
  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Set the image file
  };
  const create_Blog = async () => {
    try {
      // Create a FormData object
      const formData = new FormData();

      // Append text fields
      formData.append("title", create_data.title);
      formData.append("description", create_data.description);
      formData.append("ingredients", create_data.ingredients);
      formData.append("instructions", create_data.instructions);

      // Append image file if it exists
      if (imageFile) {
        formData.append("image", imageFile);
      }

      // Make the request with FormData
      const newData = await fetch("http://localhost:5000/Blog/create", {
        method: "POST",
        body: formData,
      });

      if (newData.ok) {
        setCreate({
          title: "",
          description: "",
          ingredients: "",
          instructions: "",
        });
        setImageFile(null); // Clear the image file state
        alert("Blog created successfully!");
      } else {
        alert("Could not add blog");
      }
    } catch (error) {
      console.log(error);
    }
  };

  const MyForm = () => {
    return (
      <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">
          Create a Blog
        </h2>
        <form>
          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm font-bold mb-2"
              htmlFor="title"
            >
              Title
            </label>
            <input
              type="text"
              id="title"
              name="title"
              placeholder="Enter the title"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
            />
          </div>

          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm font-bold mb-2"
              htmlFor="description"
            >
              Description
            </label>
            <textarea
              id="description"
              name="description"
              placeholder="Enter a brief description"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
              rows="4"
            ></textarea>
          </div>

          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm font-bold mb-2"
              htmlFor="image"
            >
              Image
            </label>
            <input
              type="file"
              id="image"
              name="image"
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white font-semibold py-2 px-4 rounded-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75"
          >
            Submit
          </button>
        </form>
      </div>
    );
  };
}

export default CreateBlogs;
