import React from "react";

function Overall({ title, ingredients, instructions, image, delete_recipe }) {
  return (
    <div className="relative max-w-md rounded-lg overflow-hidden shadow-xl bg-white border border-gray-200">
      {/* Close Button */}
      <button
        className="absolute top-4 right-4 bg-gray-100 text-gray-800 rounded-full w-8 h-8 flex items-center justify-center shadow-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-400 z-50"
        aria-label="Close"
        onClick={() => console.log("Close button clicked")} // Use the relevant close function here
      >
        &times;
      </button>

      {/* Image */}
      <img
        src={image}
        alt="Recipe"
        className="w-full h-56 object-cover rounded-t-lg"
      />

      {/* Content */}
      <div className="p-6 space-y-4 overflow-auto max-h-[70vh]">
        {/* Title */}
        <h2 className="text-2xl font-bold text-gray-900">{title}</h2>

        {/* Ingredients */}
        <div>
          <h3 className="text-lg font-semibold text-gray-800">Ingredients:</h3>
          <ul className="list-disc list-inside text-gray-600 ml-4 space-y-1">
            {ingredients.split(",").map((item, index) => (
              <li key={index}>{item.trim()}</li>
            ))}
          </ul>
        </div>

        {/* Instructions */}
        <div>
          <h3 className="text-lg font-semibold text-gray-800">Instructions:</h3>
          <p className="text-gray-700 leading-relaxed">{instructions}</p>
        </div>

        {/* Delete Button */}
      </div>
    </div>
  );
}

export default Overall;
