import React, { useEffect, useState } from "react";
import Card from "./Card";
import Overall from "./Overall";
import { useContext } from "react";
import { fav } from "./Context";
import { button_fav } from "./Context";

function AllRecipes() {
  const [result, setResult] = useState([]);
  const [particular, setParticular] = useState(null);
  const { Button_Fav, setFav_Button } = useContext(button_fav);
  const { Fav, setfav } = useContext(fav);
  const [show, setShow] = useState("Add to fav");
  const [show_button, setShow_button] = useState(true);
  const addfav = (recipe) => {
    const isFav = Fav.some((item) => item.title === recipe.title);
    if (!isFav) {
      setfav((prevFav) => [...prevFav, recipe]);
      console.log("Added to favorites:", recipe);
      setFav_Button(true);
      setShow("Remove from fav");
    } else {
      setfav((prevFav) => prevFav.filter((item) => item !== recipe));
      setFav_Button(false);
      setShow("Add to fav");
    }
  };

  const view = (recipe) => {
    setParticular(recipe);
  };

  const display = async () => {
    try {
      const response = await fetch("http://localhost:5000/blog/all/");
      const array = await response.json();
      setResult(array);
      console.log(array);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    display();
  }, []);

  const closeDetailView = () => {
    setParticular(null);
  };
  return (
    <>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 p-6 min-h-screen bg-gray-100">
        {result.map((res, index) => (
          <Card
            key={index}
            title={res.title}
            description={res.description}
            src={res.image}
            view={() => view(res)}
            addfav={() => addfav(res)}
            show={Fav.some((item) => item.title === res.title) ? "â­" : "ðŸ˜Š"}
            show_button={show_button}
          />
        ))}
      </div>

      {particular && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="relative bg-white p-6 rounded-lg shadow-lg max-w-lg w-full">
            <button
              className="absolute top-2 right-2 text-gray-500 hover:text-gray-800 text-2xl font-bold"
              onClick={closeDetailView}
            >
              &times;
            </button>
            <Overall
              title={particular.title}
              ingredients={particular.ingredients}
              instructions={particular.instructions}
              image={particular.image}
            />
          </div>
        </div>
      )}
    </>
  );
}

export default AllRecipes;
