// Context.js
import React, { createContext, useContext, useState } from "react";

// Create a context for favorites
const FavoritesContext = createContext();

// Custom hook to use the FavoritesContext
export const useFavorites = () => useContext(FavoritesContext);

// Provider component
export const FavoritesProvider = ({ children }) => {
  const [fav, setFav] = useState([]);

  // Add a recipe to favorites
  const addFavorite = (recipe) => {
    setFav((prevFav) => [...prevFav, recipe]); // Add to the array without replacing it
    console.log("Added to favorites:", recipe);
  };

  return (
    <FavoritesContext.Provider value={{ fav, addFavorite }}>
      {children}
    </FavoritesContext.Provider>
  );
};
