import { createContext } from "react";
export const fav = createContext();
export const button_fav = createContext();

export const ButtonFavProvider = ({ children }) => {
  const [Button_Fav, setFav_Button] = useState(false);
  return (
    <button_fav.Provider value={{ Button_Fav, setFav_Button }}>
      {children}
    </button_fav.Provider>
  );
};
