import React, { useState } from "react";

function CreateBlogs() {
  const [create_data, setCreate] = useState({
    title: "",
    description: "",
    ingredients: "",
    instructions: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const submit = () => {
    create_Blog();
  };
  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Set the image file
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCreate({ ...create_data, [name]: value });
  };
  const create_Blog = async () => {
    try {
      const newData = await fetch("http://localhost:5000/Blog/create", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(create_data),
      });
      if (newData.ok) {
        setCreate({
          title: "",
          description: "",
          ingredients: "",
          instructions: "",
        });
      } else if (!newData.ok) {
        alert("could not add");
      }
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form className="max-w-md w-full bg-white p-8 rounded-lg shadow-md">
        <label
          htmlFor="title"
          className="block text-gray-700 font-semibold mb-2"
        >
          Title
        </label>
        <input
          type="text"
          name="title"
          onChange={handleChange}
          placeholder="Enter your Dish's name"
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        />
        <label
          htmlFor="description"
          className="block text-gray-700 font-semibold mb-2"
        >
          Description
        </label>
        <input
          placeholder="Enter Ingredients"
          name="description"
          rows="4"
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        ></input>
        <label
          htmlFor="ingredients"
          className="block text-gray-700 font-semibold mb-2"
        >
          Ingredients
        </label>
        <input
          placeholder="Enter Ingredients"
          name="ingredients"
          rows="4"
          onChange={handleChange}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        ></input>
        <label
          htmlFor="instructions"
          className="block text-gray-700 font-semibold mb-2"
        >
          Instructions
        </label>
        <textarea
          onChange={handleChange}
          placeholder="Enter instructions"
          name="instructions"
          rows="4"
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
        ></textarea>
        <label
          htmlFor="image"
          className="block text-gray-700 font-semibold mb-2"
        >
          Food Image
        </label>
        <input type="file" accept="image/*" />{" "}
        <button
          onSubmit={submit}
          className="bg-green-500 text-white font-bold shadow-md mt-6 py-2 px-4 rounded-full hover:bg-green-700 transition duration-300"
        >
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateBlogs;
