import React, { useEffect, useState } from "react";
import Card from "./Card";
import Overall from "./Overall";
function AllRecipes() {
  const [result, setResult] = useState([]);

  const display = async () => {
    try {
      const response = await fetch("http://localhost:5000/Blog/get/");
      const array = await response.json();
      setResult(array);
      console.log(array);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    display();
  }, []);
  const view = (title, ingredients, instructions, image) => {
    return (
      <>
        <Overall
          title={title}
          ingredients={ingredients}
          instructions={instructions}
          image={image}
        />
      </>
    );
  };

  return (
    <div className="flex flex-wrap justify-center gap-6 p-6  min-h-screen bg-gray-100">
      {result.map((res, index) => (
        <Card
          key={index}
          title={res.title}
          description={res.description}
          src={res.image}
          view={view(res.title, res.ingredients, res.instructions, res.image)}
        />
      ))}
    </div>
  );
}

export default AllRecipes;
