import React from "react";

function Overall({ title, ingredients, instructions, image, delete_recipe }) {
  return (
    <div className="max-w-md rounded-lg overflow-hidden shadow-xl bg-white border border-gray-200">
      {/* Image */}
      <img
        src={image}
        alt="Recipe"
        className="w-full h-56 object-cover rounded-t-lg"
      />

      {/* Content */}
      <div className="p-6 space-y-4">
        {/* Title */}
        <h2 className="text-2xl font-bold text-gray-900">{title}</h2>

        {/* Ingredients */}
        <div>
          <h3 className="text-lg font-semibold text-gray-800">Ingredients:</h3>
          <ul className="list-disc list-inside text-gray-600 ml-4 space-y-1">
            {ingredients.split(",").map((item, index) => (
              <li key={index}>{item.trim()}</li>
            ))}
          </ul>
        </div>

        {/* Instructions */}
        <div>
          <h3 className="text-lg font-semibold text-gray-800">Instructions:</h3>
          <p className="text-gray-700 leading-relaxed">{instructions}</p>
        </div>

        {/* Delete Button */}
        <button
          className="bg-red-500 text-white font-semibold shadow-lg py-2 px-6 rounded-full hover:bg-red-600 active:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-offset-2 transition duration-300"
          onClick={delete_recipe}
        >
          Delete
        </button>
      </div>
    </div>
  );
}

export default Overall;
