import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function EditRecipe() {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    // Fetch the recipe data using the id from the URL
    const fetchRecipe = async () => {
      try {
        const response = await fetch(`http://localhost:5000/Blog/${id}`);
        const data = await response.json();
        setRecipe(data);
      } catch (error) {
        console.error("Error fetching recipe:", error);
      }
    };

    fetchRecipe();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/Blog/update/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(recipe),
      });

      if (response.ok) {
        alert("Recipe updated successfully");
      } else {
        alert("Failed to update recipe");
      }
    } catch (error) {
      console.error("Error updating recipe:", error);
      alert("Error occurred while updating recipe");
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe((prevRecipe) => ({
      ...prevRecipe,
      [name]: value,
    }));
  };

  if (!recipe) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Edit Recipe</h1>
      <form onSubmit={handleUpdate}>
        <label>
          Title:
          <input
            type="text"
            name="title"
            value={recipe.title}
            onChange={handleChange}
          />
        </label>
        <label>
          Description:
          <input
            type="text"
            name="description"
            value={recipe.description}
            onChange={handleChange}
          />
        </label>
        <label>
          Ingredients:
          <textarea
            name="ingredients"
            value={recipe.ingredients}
            onChange={handleChange}
          ></textarea>
        </label>
        <label>
          Instructions:
          <textarea
            name="instructions"
            value={recipe.instructions}
            onChange={handleChange}
          ></textarea>
        </label>
        <label>
          Image URL:
          <input
            type="text"
            name="image"
            value={recipe.image}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Update Recipe</button>
      </form>
    </div>
  );
}

export default EditRecipe;
