import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function CreateBlogs() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const [create_data, setCreate] = useState({
    title: "",
    description: "",
    ingredients: "",
    instructions: "",
    image: "",
  });

  const submit = (event) => {
    event.preventDefault(); // Prevent form from refreshing the page
    create_Blog();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCreate({ ...create_data, [name]: value });
  };

  const create_Blog = async () => {
    try {
      const response = await fetch("http://localhost:5000/Blog/create/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(create_data),
      });

      if (response.ok) {
        setCreate({
          title: "",
          description: "",
          ingredients: "",
          instructions: "",
          image: "",
        });
        navigate("/");
      } else {
        alert("Could not add the blog");
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <form
        onSubmit={submit}
        className="max-w-md w-full bg-white p-8 rounded-lg shadow-md"
      >
        <label
          htmlFor="title"
          className="block text-gray-700 font-semibold mb-2"
        >
          Title
        </label>
        <input
          type="text"
          name="title"
          onChange={handleChange}
          value={create_data.title}
          placeholder="Enter your Dish's name"
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        />

        <label
          htmlFor="description"
          className="block text-gray-700 font-semibold mb-2"
        >
          Description
        </label>
        <input
          placeholder="Enter Description"
          name="description"
          onChange={handleChange}
          value={create_data.description}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        />

        <label
          htmlFor="ingredients"
          className="block text-gray-700 font-semibold mb-2"
        >
          Ingredients
        </label>
        <input
          placeholder="Enter Ingredients"
          name="ingredients"
          onChange={handleChange}
          value={create_data.ingredients}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        />

        <label
          htmlFor="instructions"
          className="block text-gray-700 font-semibold mb-2"
        >
          Instructions
        </label>
        <textarea
          onChange={handleChange}
          placeholder="Enter instructions"
          name="instructions"
          value={create_data.instructions}
          rows="4"
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        ></textarea>

        <label
          htmlFor="image"
          className="block text-gray-700 font-semibold mb-2"
        >
          Add Image Link in png or jpg format
        </label>
        <input
          placeholder="Enter Image Link"
          name="image"
          onChange={handleChange}
          value={create_data.image}
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
        />

        <button
          type="submit"
          className="bg-green-500 text-white font-bold shadow-md mt-6 py-2 px-4 rounded-full hover:bg-green-700 transition duration-300"
        >
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateBlogs;
