import React, { useEffect, useState } from "react";
import Card from "./Card";
import Overall from "./Overall";
function AllRecipes() {
  const [result, setResult] = useState([]);
  const [particular, setParticular] = useState(null);
  const view = (recipe) => {
    setParticular(recipe);
  };

  const display = async () => {
    try {
      const response = await fetch("http://localhost:5000/Blog/get/");
      const array = await response.json();
      setResult(array);
      console.log(array);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    display();
  }, []);
  const closeDetailView = () => {
    setParticular(null);
  };

  return (
    <>
      <div className="flex flex-wrap justify-center gap-6 p-6  min-h-screen bg-gray-100">
        {result.map((res, index) => (
          <Card
            key={index}
            title={res.title}
            description={res.description}
            src={res.image}
            view={() => {
              view(res);
            }}
          />
        ))}
      </div>
      <div>
        {particular && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="relative bg-white p-6 rounded-lg shadow-lg max-w-lg w-full">
              <button
                className="absolute top-2 right-2 text-gray-500 hover:text-gray-800"
                onClick={closeDetailView}
              >
                &times;
              </button>
              <Overall
                title={particular.title}
                ingredients={particular.ingredients}
                instructions={particular.instructions}
                image={particular.image}
              />
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default AllRecipes;
