import React, { useState } from "react";

function CreateBlogs() {
  const [create_data, setCreate] = useState({
    title: "",
    description: "",
    ingredients: "",
    instructions: "",
  });
  const [imageFile, setImageFile] = useState(null); // New state for image file

  // Handle form submission
  const submit = () => {
    create_Blog();
  };

  // Handle text input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setCreate({ ...create_data, [name]: value });
  };

  // Handle image file input change
  const handleImageChange = (e) => {
    setImageFile(e.target.files[0]); // Set the image file
  };
  const create_Blog = async () => {
    try {
      // Create a FormData object
      const formData = new FormData();

      // Append text fields
      formData.append("title", create_data.title);
      formData.append("description", create_data.description);
      formData.append("ingredients", create_data.ingredients);
      formData.append("instructions", create_data.instructions);

      // Append image file if it exists
      if (imageFile) {
        formData.append("image", imageFile);
      }

      // Make the request with FormData
      const newData = await fetch("http://localhost:5000/Blog/create", {
        method: "POST",
        body: formData,
      });

      if (newData.ok) {
        setCreate({
          title: "",
          description: "",
          ingredients: "",
          instructions: "",
        });
        setImageFile(null); // Clear the image file state
        alert("Blog created successfully!");
      } else {
        alert("Could not add blog");
      }
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      {" "}
      <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
        {" "}
        <h2 className="text-2xl font-semibold text-gray-800 mb-6">
          Create a Blog
        </h2>
        <form onSubmit={submit}>
          <input
            type="text"
            name="title"
            value={create_data.title}
            onChange={handleChange}
            placeholder="Title"
            required
          />
          <input
            type="text"
            name="description"
            value={create_data.description}
            onChange={handleChange}
            placeholder="Description"
            required
          />
          <input
            type="text"
            name="ingredients"
            value={create_data.ingredients}
            onChange={handleChange}
            placeholder="Ingredients"
            required
          />
          <input
            type="text"
            name="instructions"
            value={create_data.instructions}
            onChange={handleChange}
            placeholder="Instructions"
            required
          />
          <input
            type="file"
            name="image"
            onChange={handleImageChange} // Handle image file selection
            accept="image/*"
            required
          />
          <button type="button" onClick={submit}>
            Submit
          </button>
        </form>
      </div>
    </>
  );
}

export default CreateBlogs;
